import{r as s,o as i,c as r,a as e,b as o,F as d,e as n,d as a}from"./app.292a7b1f.js";import{_ as l}from"./plugin-vue_export-helper.21dcd24c.js";const c={},u=n('<h1 id="language" tabindex="-1"><a class="header-anchor" href="#language" aria-hidden="true">#</a> Language</h1><p>Chevereto uses <code>gettext</code> for its translations and the system comes with a lot of languages built-in which you can find in the <code>app/content/languages</code> folder.</p><h2 id="add-new-languages" tabindex="-1"><a class="header-anchor" href="#add-new-languages" aria-hidden="true">#</a> Add new languages</h2><h3 id="onesky" tabindex="-1"><a class="header-anchor" href="#onesky" aria-hidden="true">#</a> OneSky</h3>',4),h=a("If you want to add a brand new language we encourage you to apply to "),g={href:"http://translate.chevereto.com/",target:"_blank",rel:"noopener noreferrer"},p=a("translation"),m=a(", where you can easily contribute a new language. OneSky features a complete suite for translations with no additional software required."),_=e("div",{class:"custom-container tip"},[e("p",{class:"custom-container-title"},"TIP"),e("p",null,"Translations contributed will get added to the software.")],-1),f=e("h3",{id:"manually",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#manually","aria-hidden":"true"},"#"),a(" Manually")],-1),y=e("p",null,[a("Put your "),e("code",null,".po"),a(" files at "),e("code",null,"app/content/languages"),a(" folder. The language code must be expressed in ISO format.")],-1),b={class:"custom-container warning"},w=e("p",{class:"custom-container-title"},"WARNING",-1),v=a("Considering using a "),x={href:"https://www.google.com/search?q=po%20editor",target:"_blank",rel:"noopener noreferrer"},q=a(".po editor software"),k=a(" to create the translation file."),I=n(`<h2 id="customizing-language-strings" tabindex="-1"><a class="header-anchor" href="#customizing-language-strings" aria-hidden="true">#</a> Customizing language strings</h2><p>All language strings can be customized to fit what you want to show to your visitors. That way, you don&#39;t need to touch the theme at all.</p><p>The system works by overriding the target translation. For example, replacing <code>Upload and share your images.</code> to &quot;Upload, do it now!&quot; by doing the following procedure:</p><h3 id="_1-get-the-translation-string-msgid-and-msgstr-values" tabindex="-1"><a class="header-anchor" href="#_1-get-the-translation-string-msgid-and-msgstr-values" aria-hidden="true">#</a> 1. Get the translation string (<code>msgid</code> and <code>msgstr</code> values)</h3><ul><li>Go to the <code>app/content/languages</code> folder</li><li>Open the file <code>en.po</code> (in this example we are overriding English language)</li><li>Find this text: <code>Upload and share your images.</code></li><li>Copy the <code>msgid</code> and the <code>msgstr</code> lines, you should get something like this:</li></ul><div class="language-po ext-po line-numbers-mode"><pre class="language-po"><code>msgid &quot;Upload and share your images.&quot;
msgstr &quot;&quot;
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>The property <code>msgstr</code> is empty because English is the base language. If you open any other language you will see that value actually translated.</p></div><h3 id="_2-override-translation-string" tabindex="-1"><a class="header-anchor" href="#_2-override-translation-string" aria-hidden="true">#</a> 2. Override translation string</h3><ul><li>Go to the <code>app/content/languages/overrides</code> folder</li><li>Create the file <code>en.po</code> with these contents:</li></ul><div class="language-po ext-po line-numbers-mode"><pre class="language-po"><code>msgid &quot;Upload and share your images.&quot;
msgstr &quot;Upload, do it now!&quot;
</code></pre><div class="line-numbers"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>You only need to replace <code>msgstr</code> because that is the translation string.</p></div><p>The result is that the system now will display <code>Upload, do it now!</code> instead of the default <code>Upload and share your images.</code> and it will only affect that language.</p><div class="custom-container warning"><p class="custom-container-title">WARNING</p><p><strong>Note:</strong> You don&#39;t actually need a gettext editor for language overrides, but make sure to use double-quotes. If you need to put a double quote inside <code>msgstr</code> use <code>\\&quot;</code> (escaped double-quote).</p></div>`,13);function T(N,U){const t=s("ExternalLinkIcon");return i(),r(d,null,[u,e("p",null,[h,e("a",g,[p,o(t)]),m]),_,f,y,e("div",b,[w,e("p",null,[v,e("a",x,[q,o(t)]),k])]),I],64)}var G=l(c,[["render",T]]);export{G as default};
