import{e as t}from"./app.c1a8397c.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const i={},a=t('<h1 id="listing-settings" tabindex="-1"><a class="header-anchor" href="#listing-settings" aria-hidden="true">#</a> Listing settings</h1><p><code>/dashboard/settings/listings</code></p><h2 id="list-items-per-page" tabindex="-1"><a class="header-anchor" href="#list-items-per-page" aria-hidden="true">#</a> List items per page</h2><p>Determines how many items will be displayed per page. The more you display, the slower it gets. Choose wisely.</p><table><thead><tr><th>Type</th><th>Recommended</th></tr></thead><tbody><tr><td>Integer</td><td>24</td></tr></tbody></table><h2 id="list-pagination-mode" tabindex="-1"><a class="header-anchor" href="#list-pagination-mode" aria-hidden="true">#</a> List pagination mode</h2><p>Determines how pagination will work.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Classic pagination</td><td>Use conventional pages to browse listings</td></tr><tr><td>Endless scrolling</td><td>Use XHR to append content to the listing page</td></tr></tbody></table><h2 id="listing-viewer" tabindex="-1"><a class="header-anchor" href="#listing-viewer" aria-hidden="true">#</a> Listing viewer</h2><p>Toggles the listing viewer.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Enabled</td><td>Enables a a full-screen image browsing experience, like a lightbox</td></tr><tr><td>Disabled</td><td>No viewer</td></tr></tbody></table><h2 id="image-listing-size" tabindex="-1"><a class="header-anchor" href="#image-listing-size" aria-hidden="true">#</a> Image listing size</h2><p>Determines how images will be fitted in listings.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Fixed</td><td>Tiled listing display, fixed width and height</td></tr><tr><td>Fluid</td><td>Masonry listing display, automatic height</td></tr></tbody></table><h2 id="album-listing-images-requirement-explore" tabindex="-1"><a class="header-anchor" href="#album-listing-images-requirement-explore" aria-hidden="true">#</a> Album listing images requirement (explore)</h2><p>Determine the album image count required to show the album in listings.</p><table><thead><tr><th>Type</th><th>Recommended</th></tr></thead><tbody><tr><td>Integer</td><td>5</td></tr></tbody></table><h2 id="listing-columns-number" tabindex="-1"><a class="header-anchor" href="#listing-columns-number" aria-hidden="true">#</a> Listing columns number</h2><p>Determines the number of columns used for listing. You can control this for a wide selection of devices: phone, phablet, tablet, laptop and desktop.</p><table><thead><tr><th>Type</th><th>Recommended</th></tr></thead><tbody><tr><td>Integer</td><td><code>1</code> or <code>2</code> columns for small devices</td></tr></tbody></table>',20);function d(r,s){return a}var l=e(i,[["render",d]]);export{l as default};
