import{e}from"./app.32562cb7.js";import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";const a={},d=e('<h1 id="website-settings" tabindex="-1"><a class="header-anchor" href="#website-settings" aria-hidden="true">#</a> Website settings</h1><p><code>/dashboard/settings/website</code></p><h2 id="website-name" tabindex="-1"><a class="header-anchor" href="#website-name" aria-hidden="true">#</a> Website name</h2><p>Determines the name of your website, it will be used in all document titles (trailing).</p><h2 id="website-doctitle" tabindex="-1"><a class="header-anchor" href="#website-doctitle" aria-hidden="true">#</a> Website <code>doctitle</code></h2><p>Determines the <code>doctitle</code> for your website, mainly used in homepage.</p><h2 id="website-description" tabindex="-1"><a class="header-anchor" href="#website-description" aria-hidden="true">#</a> Website description</h2><p>A brief summary on what&#39;s your website.</p><h2 id="powered-by-chevereto" tabindex="-1"><a class="header-anchor" href="#powered-by-chevereto" aria-hidden="true">#</a> Powered by Chevereto</h2><p>Determines if the &quot;Powered by Chevereto&quot; appears on your website.</p><p>Showing &quot;Powered by Chevereto&quot; allows others to know the software and it helps us to get known.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Enabled</td><td>Enable &quot;Powered by Chevereto&quot; footer</td></tr><tr><td>Disabled</td><td>Disable &quot;Powered by Chevereto&quot; footer</td></tr></tbody></table><h2 id="https" tabindex="-1"><a class="header-anchor" href="#https" aria-hidden="true">#</a> HTTPS</h2><p>Determines how HTTPS will be handled for the application URLs.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Automatic</td><td>Use HTTPS on URLs if web server is configured with HTTPS</td></tr><tr><td>Forced</td><td>Always use HTTPS</td></tr><tr><td>Disabled</td><td>Disable HTTPS (all URLs will use <code>http</code>)</td></tr></tbody></table><h2 id="default-time-zone" tabindex="-1"><a class="header-anchor" href="#default-time-zone" aria-hidden="true">#</a> Default time zone</h2><p>Determines the default system time zone. This will be the default timezone for new user sign-up.</p><h2 id="search" tabindex="-1"><a class="header-anchor" href="#search" aria-hidden="true">#</a> Search</h2><p>Toggles the search functionality.</p><p>Search allows users to locate content based on a search query.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Enabled</td><td>Enable search</td></tr><tr><td>Disabled</td><td>Disable search</td></tr></tbody></table><h2 id="explore" tabindex="-1"><a class="header-anchor" href="#explore" aria-hidden="true">#</a> Explore</h2><p>Toggles the explore functionality.</p><p>Explore browses the public available images.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Enabled</td><td>Enable explore</td></tr><tr><td>Disabled</td><td>Disable explore</td></tr></tbody></table><h2 id="explore-guests" tabindex="-1"><a class="header-anchor" href="#explore-guests" aria-hidden="true">#</a> Explore (guests)</h2><p>Toggles the explore functionality (for guests).</p><p>Same as <a href="#explore">explore</a>, but only affects guests users.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Enabled</td><td>Enable explore for guest</td></tr><tr><td>Disabled</td><td>Disable explore for guest</td></tr></tbody></table><h2 id="random" tabindex="-1"><a class="header-anchor" href="#random" aria-hidden="true">#</a> Random</h2><p>Toggles the random functionality.</p><p>Random allows to randomly show a public image. It enables <code>?random</code> to fetch a random public image.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Enabled</td><td>Enable random button</td></tr><tr><td>Disabled</td><td>Disable random button</td></tr></tbody></table><h2 id="likes" tabindex="-1"><a class="header-anchor" href="#likes" aria-hidden="true">#</a> Likes</h2><p>Toggles the likes functionality.</p><p>Likes allow users to like content. It also generates &quot;liked&quot; listings.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Enabled</td><td>Enable likes</td></tr><tr><td>Disabled</td><td>Disable likes</td></tr></tbody></table><h2 id="followers" tabindex="-1"><a class="header-anchor" href="#followers" aria-hidden="true">#</a> Followers</h2><p>Toggles the followers functionality.</p><p>Followers allow users to follow each other. It also enables the &quot;following&quot; listing for each user.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Enabled</td><td>Enable followers</td></tr><tr><td>Disabled</td><td>Disable followers</td></tr></tbody></table><h2 id="website-mode" tabindex="-1"><a class="header-anchor" href="#website-mode" aria-hidden="true">#</a> Website mode</h2><p>Determines the website mode. It will affect how your installation works.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Community</td><td>A community-like website with users interacting</td></tr><tr><td>Personal</td><td>A single-user experience</td></tr></tbody></table><h2 id="website-privacy-mode" tabindex="-1"><a class="header-anchor" href="#website-privacy-mode" aria-hidden="true">#</a> Website privacy mode</h2><p>Determines the website privacy mode.</p><div class="custom-container warning"><p class="custom-container-title">WARNING</p><p>Private mode will always require login. Implement it only for private audiences.</p></div><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Public</td><td>All content is intended to be public</td></tr><tr><td>Private</td><td>All content is intended to be private (login required)</td></tr></tbody></table><h2 id="content-privacy-mode" tabindex="-1"><a class="header-anchor" href="#content-privacy-mode" aria-hidden="true">#</a> Content privacy mode*</h2><p>*Applies only if <code>Website privacy mode</code> is set to <code>Private</code>.</p><p>Determines the user content privacy mode.</p><p>Privacy is provisioned per album basis. Images inherits privacy from the album.</p><table><thead><tr><th>Value</th><th>Effect</th></tr></thead><tbody><tr><td>Default</td><td>Users can freely choose the privacy for their own content</td></tr><tr><td>Force private (self)</td><td>User content is forced to be private and only the owner can see the content</td></tr><tr><td>Force private (anyone with the link)</td><td>User content is forced to be private but accesible for anyone with the link</td></tr></tbody></table>',53);function r(o,i){return d}var l=t(a,[["render",r]]);export{l as default};
